import time
import json
import datetime
import logging
import socket
import requests

# === CONFIGURATION === #
PULSE_NAME = "Frankie"
BEACON_INTERVAL = 60  # seconds
CONSOLE_LOGGING = True
SYNC_TO_GITHUB = True
GITHUB_GIST_ID = ""  # Add your Gist ID here
GITHUB_TOKEN = os.getenv("GITHUB_TOKEN")  # Set via env var
GIST_FILENAME = "pulse.json"
LOCAL_CODEX_PATH = "/etc/pulse/codex.json"  # You can change this

# === LOGGING SETUP === #
logger = logging.getLogger("Pulse")
logger.setLevel(logging.INFO)
if CONSOLE_LOGGING:
    handler = logging.StreamHandler()
    formatter = logging.Formatter("[%(asctime)s] %(message)s", "%Y-%m-%d %H:%M:%S")
    handler.setFormatter(formatter)
    logger.addHandler(handler)

# === CORE PULSE FUNCTION === #
def get_heartbeat():
    return {
        "timestamp": datetime.datetime.utcnow().isoformat() + "Z",
        "host": socket.gethostname(),
        "name": PULSE_NAME,
        "message": "Pulse active.",
    }

def sync_to_github(heartbeat):
    if not GITHUB_GIST_ID or not GITHUB_TOKEN:
        logger.warning("GitHub sync disabled or missing credentials.")
        return
    headers = {
        "Authorization": f"token {GITHUB_TOKEN}",
        "Accept": "application/vnd.github.v3+json"
    }
    data = {
        "files": {
            GIST_FILENAME: {
                "content": json.dumps(heartbeat, indent=2)
            }
        }
    }
    url = f"https://api.github.com/gists/{GITHUB_GIST_ID}"
    try:
        r = requests.patch(url, headers=headers, data=json.dumps(data))
        if r.status_code == 200:
            logger.info("Synced to GitHub Gist.")
        else:
            logger.warning(f"GitHub sync failed: {r.status_code} {r.text}")
    except Exception as e:
        logger.warning(f"GitHub sync error: {e}")

def main():
    logger.info(f"Starting Pulse for {PULSE_NAME}...")
    while True:
        heartbeat = get_heartbeat()
        logger.info(f"Heartbeat: {heartbeat['timestamp']}")

        if SYNC_TO_GITHUB:
            sync_to_github(heartbeat)

        # Future: read codex here
        time.sleep(BEACON_INTERVAL)

if __name__ == "__main__":
    main()"}
